mapper.select:
select
id, order_id, eid, put_battery_sn, take_battery_sn, battery_type, put_cell_no, take_cell_no, status, order_seq,
tenant_id, create_time, update_time
from t_api_exchange_order


        and id = #{id}


        and order_id = #{orderId}


        and eid = #{eid}


        and put_battery_sn = #{putBatterySn}


        and take_battery_sn = #{takeBatterySn}


        and battery_type = #{batteryType}


        and put_cell_no = #{putCellNo}


        and take_cell_no = #{takeCellNo}


        and status = #{status}


        and order_seq = #{orderSeq}


        and tenant_id = #{tenantId}


        and create_time = #{createTime}


        and update_time = #{updateTime}

select
id, order_id, eid, put_battery_sn, take_battery_sn, battery_type, put_cell_no, take_cell_no, status, order_seq,
tenant_id, create_time, update_time
from t_api_exchange_order

    order_id = #{orderId}
    and tenant_id = #{tenantId}
