mapper.select:
select
id, name, config_content, create_time, update_time, tenant_id, del_flag
from t_app_param_setting_template


        and id = #{id}


        and name = #{name}


        and config_content = #{configContent}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and tenant_id = #{tenantId}


        and del_flag = #{delFlag}
