mapper.select:
select
  id, b_id, sn, battery_v, battery_a, soc, capacity, battery_status, core_num, hardware_temp, core_temp, env_temp, battery_core_v_list, battery_health_part_one, battery_health_part_two, battery_health_part_three, battery_health_part_four, battery_health_part_five, battery_health_part_six, battery_health_part_seven, create_time, update_time
from t_battery_attr


        and id = #{id}


        and b_id = #{bId}


        and sn = #{sn}


        and battery_v = #{batteryV}


        and battery_a = #{batteryA}


        and soc = #{soc}


        and capacity = #{capacity}


        and battery_status = #{batteryStatus}


        and core_num = #{coreNum}


        and hardware_temp = #{hardwareTemp}


        and core_temp = #{coreTemp}


        and env_temp = #{envTemp}


        and battery_core_v_list = #{batteryCoreVList}


        and battery_health_part_one = #{batteryHealthPartOne}


        and battery_health_part_two = #{batteryHealthPartTwo}


        and battery_health_part_three = #{batteryHealthPartThree}


        and battery_health_part_four = #{batteryHealthPartFour}


        and battery_health_part_five = #{batteryHealthPartFive}


        and battery_health_part_six = #{batteryHealthPartSix}


        and battery_health_part_seven = #{batteryHealthPartSeven}


        and create_time = #{createTime}


        and update_time = #{updateTime}
