mapper.select:
select
  id, sn, longitude, latitude, gis, geohash, tenant_id, create_time, update_time
from t_battery_geo


        and id = #{id}


        and sn = #{sn}


        and longitude = #{longitude}


        and latitude = #{latitude}


        and gis = #{gis}


        and geohash = #{geohash}


        and tenant_id = #{tenantId}


        and create_time = #{createTime}


        and update_time = #{updateTime}

mapper.insert:
insert into t_battery_geo(sn, longitude, latitude, gis,  tenant_id, create_time, update_time,franchisee_id)
values (#{sn}, #{longitude}, #{latitude},ST_GeometryFromText(CONCAT('POINT(', longitude, ' ', latitude, ')')) , #{tenantId}, #{createTime}, #{updateTime},#{franchiseeId})
 on duplicate key update longitude=#{longitude},latitude= #{latitude},update_time=#{updateTime},gis=ST_GeometryFromText(CONCAT('POINT(', longitude, ' ', latitude, ')')),franchisee_id=#{franchiseeId}

mapper.select:
select id, sn, longitude, latitude, geohash, tenant_id, create_time, update_time,franchisee_id from t_battery_geo
tenant_id = #{tenantId} and
geohash like concat(#{i},'%')
and st_distance_sphere(st_geomfromtext(concat('point(',#{lon},' ',#{lat},')')),gis) < #{size}
and franchisee_id in
#{id}
