mapper.select:
select tbm.id, bm.name as material, tbm.battery_model, tbm.battery_type, tbm.battery_v, tbm.battery_v_short, tbm.create_time, tbm.update_time, tbm.type from t_battery_model tbm left join t_battery_material bm on tbm.mid=bm.id where tbm.del_flag=0
and tbm.battery_type = #{batteryType}
and tbm.tenant_id = #{tenantId}
order by tbm.id desc limit #{offset}, #{size}

select
count(1)
from t_battery_model
where del_flag=0

    and battery_type = #{batteryType}


    and tenant_id = #{tenantId}

select
battery_v_short
from t_battery_model
where del_flag=0

    and battery_type = #{batteryType}


    and battery_type in

        #{id}



    and tenant_id = #{tenantId}

select
id, mid,battery_model, battery_type, battery_v, battery_v_short, del_flag, type, tenant_id, create_time, update_time
from t_battery_model


        and id = #{id}


        and battery_model = #{batteryModel}


        and battery_type = #{batteryType}


        and battery_v = #{batteryV}


        and battery_v_short = #{batteryVShort}


        and del_flag = #{delFlag}


        and tenant_id = #{tenantId}


        and create_time = #{createTime}


        and update_time = #{updateTime}
