mapper.select:
select
id, name, config, create_time, update_time, tenant_id, del_flag
from t_battery_param_setting_template


        and id = #{id}


        and name = #{name}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and tenant_id = #{tenantId}


        and del_flag = #{delFlag}

select id, config, name, create_time, update_time, tenant_id, del_flag from t_battery_param_setting_template
del_flag = 0
and name like concat('%', #{name}, '%')
and tenant_id = #{tenantId}
limit #{offset}, #{size}
