mapper.select:
select
id, uid, name, phone, status, type, car_id, car_sn, car_model, car_model_id, create_time,
update_time, tenant_id
from t_car_lock_ctrl_history


        and id = #{id}


        and uid = #{uid}


        and name = #{name}


        and phone = #{phone}


        and status = #{status}


        and type = #{type}


        and car_id = #{carId}


        and car_sn = #{carSn}


        and car_model = #{carModel}


        and car_model_id = #{carModelId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and tenant_id = #{tenantId}

select clch.id, clch.uid, clch.name, clch.phone, clch.status, clch.type, clch.car_id, clch.car_sn, clch.car_model, clch.car_model_id, clch.create_time, clch.update_time from t_car_lock_ctrl_history clch left join t_electricity_car tec on clch.car_id=tec.id
clch.tenant_id = #{tenantId}
and clch.name like concat('%', #{name}, '%')
and clch.phone like concat('%', #{phone}, '%')
and clch.uid = #{uid}
and clch.car_sn like concat('%', #{carSn}, '%')
and clch.create_time > #{beginTime}
and clch.create_time < #{endTime}
and tec.franchisee_id in
#{id}
and tec.store_id in
#{id}
order by clch.create_time desc limit #{offset}, #{size}

select
count(*)
from t_car_lock_ctrl_history clch
left join t_electricity_car tec on clch.car_id=tec.id

    clch.tenant_id = #{tenantId}

        and clch.name like concat('%', #{name}, '%')


