mapper.select:
select
id, order_id, uid, name, phone, car_id, car_sn, car_deposit, car_model_id, status, remark, deposit_order_id
create_time, update_time, store_id, tenant_id
from t_car_refund_order


        and id = #{id}


        and order_id = #{orderId}


        and uid = #{uid}


        and name = #{name}


        and phone = #{phone}


        and car_id = #{carId}


        and car_sn = #{carSn}


        and car_deposit = #{carDeposit}


        and car_model_id = #{carModelId}


        and status = #{status}


        and remark = #{remark}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and store_id = #{storeId}


        and tenant_id = #{tenantId}

select
id, order_id, uid, `name`, phone, car_id, car_sn, car_deposit, car_model_id, status, remark, create_time ,
deposit_order_id
update_time, store_id, tenant_id
from t_car_refund_order



        and order_id = #{orderId}


        and `name` = #{userName}


        and phone = #{phone}


        and store_id = #{storeId}


order by create_time desc
limit #{offset}, #{size}

select
count(*)
from t_car_refund_order


        and order_id = #{orderId}


        and `name` = #{userName}


        and phone = #{phone}


        and store_id = #{storeId}

select count(*)
from t_car_refund_order
where uid = #{uid} and tenant_id = #{tenantId}

    and status = #{status}
