mapper.select:
select
id, uid, invite_uid, channel_uid, status, create_time, update_time, tenant_id
from t_channel_activity_history


        and id = #{id}


        and uid = #{uid}


        and invite_uid = #{inviteUid}


        and channel_uid = #{channelUid}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and tenant_id = #{tenantId}

select
tcah.id, tcah.uid, tcah.invite_uid, tcah.channel_uid, tcah.status, tcah.create_time, tcah.update_time,
tcah.tenant_id, tui.phone as phone, tui.name as name
from t_channel_activity_history tcah
left join t_user_info tui on tui.uid = tcah.uid


        and tui.phone like concat('%', #{phone}, '%')


        and tcah.tenant_id = #{tenantId}


        and tcah.create_time >= #{beginTime}


        and tcah.create_time <= #{endTime}


order by create_time desc
limit #{offset}, #{size}

select
count(*)
from t_channel_activity_history tcah
left join t_user_info tui on tui.uid = tcah.uid


        and tui.phone like concat('%', #{phone}, '%')


        and tcah.tenant_id = #{tenantId}


        and tcah.create_time >= #{beginTime}


        and tcah.create_time <= #{endTime}

select tcah.id, tcah.uid, tcah.invite_uid, tcah.channel_uid, tcah.status, tcah.create_time, tcah.update_time, tcah.tenant_id, tui.phone as phone, tui.name as name from t_channel_activity_history tcah left join t_user_info tui on tui.uid = tcah.uid left join t_user_info tu on tu.uid = tcah.channel_uid
and tui.phone like concat('%', #{phone}, '%')
and tcah.tenant_id = #{tenantId}
and tui.uid = #{uid}
and tcah.create_time >= #{beginTime}
and tcah.create_time <= #{endTime}
and tu.franchisee_id in
#{id}
and tu.store_id in
#{id}
order by id desc limit #{offset}, #{size}

select
    count(*)
from t_channel_activity_history tcah
left join t_user_info tui on tui.uid = tcah.uid
left join t_user_info tu on tu.uid = tcah.channel_uid


        and tui.phone like concat('%', #{phone}, '%')


        and tcah.tenant_id = #{tenantId}


        and tui.uid = #{uid}


        and tcah.create_time >= #{beginTime}


        and tcah.create_time <= #{endTime}


        and tu.franchisee_id in

            #{id}



        and tu.store_id in

            #{id}
