mapper.select:
select
id, name, valid_day, valid_day_limit, status, bind_activity_id, bind_activity_type, create_time,
update_time, tenant_id
from t_channel_activity


        and id = #{id}


        and name = #{name}


        and valid_day = #{validDay}


        and valid_day_limit = #{validDayLimit}


        and status = #{status}


        and bind_activity_id = #{bindActivityId}


        and bind_activity_type = #{bindActivityType}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and tenant_id = #{tenantId}

select
  id, name, valid_day, valid_day_limit, status, bind_activity_id, bind_activity_type, create_time, update_time, tenant_id
from t_channel_activity
where tenant_id = #{tenantId}
limit #{offset}, #{size}
