mapper.update:
update t_coupon_day_record
set del_flag = 1
where del_flag = 0
AND tenant_id = #{tenantId}
AND uid = #{uid}
AND package_order = #{orderNo}
AND use_scope = #{scope}

mapper.select:
select sum(IFNULL(days,0)) from t_coupon_day_record
where del_flag = 0
AND tenant_id = #{tenantId}
AND uid = #{uid}
AND package_order = #{orderNo}
AND use_scope = #{scope}
