mapper.select:
select
id, coupon_id,uid,name,operate_name,phone,create_time,update_time
from t_coupon_issue_operate_record


        and coupon_id = #{query.couponId}


        and uid = #{query.uid}


        and phone =#{query.phone}


        and name =#{query.name}


        and create_time >=#{query.beginTime}


        and create_time <=#{query.endTime}


        and tenant_id = #{query.tenantId}


order by create_time desc

    limit #{query.offset}, #{query.size}

select COUNT(1)
from t_coupon_issue_operate_record


        and coupon_id = #{query.couponId}


        and uid = #{query.uid}


        and phone =#{query.phone}


        and name =#{query.name}


        and create_time >=#{query.beginTime}


        and create_time <=#{query.endTime}


        and tenant_id = #{query.tenantId}

select
    tcior.id,
    tcior.coupon_id,
    tcior.uid,
    tui.name,
    tcior.operate_name,
    tcior.issued_uid,
    tui.phone,
    tcior.create_time,
    tcior.update_time
from t_coupon_issue_operate_record tcior
left join t_user_info tui on tui.uid = tcior.uid


        and tcior.coupon_id = #{query.couponId}


        and tcior.uid = #{query.uid}


        and tui.phone =#{query.phone}


        and tui.name =#{query.name}


        and tcior.create_time >=#{query.beginTime}


        and tcior.create_time <=#{query.endTime}


        and tcior.tenant_id = #{query.tenantId}



order by tcior.create_time desc

    limit #{query.offset}, #{query.size}

select count(1)
from t_coupon_issue_operate_record tcior
left join t_user_info tui on tui.uid = tcior.uid


        and tcior.coupon_id = #{query.couponId}


        and tcior.uid = #{query.uid}


        and tui.phone =#{query.phone}


        and tui.name =#{query.name}


        and tcior.create_time >=#{query.beginTime}


        and tcior.create_time <=#{query.endTime}


        and tcior.tenant_id = #{query.tenantId}
