mapper.select:
select
  id, name, status, apply_type,  discount_type, amount, discount, count, days,  description,
  uid, user_name, create_time, update_time, del_flag,franchisee_id,type
from t_coupon


        and name = #{query.name}


        and discount_type = #{query.discountType}


        and franchisee_id =#{query.franchiseeId}


        and apply_type =#{query.applyType}


        and tenant_id = #{query.tenantId}


order by create_time desc

    limit #{query.offset}, #{query.size}

select  COUNT(1)
from t_coupon


        and name = #{query.name}


        and discount_type = #{query.discountType}


        and franchisee_id =#{query.franchiseeId}


        and apply_type =#{query.applyType}


        and tenant_id = #{query.tenantId}
