mapper.select:
select
  id, name, status, apply_type,  discount_type, amount, discount, count, days,  description,
  uid, user_name, create_time, update_time, del_flg,franchisee_id
from t_coupon


        and name = #{query.name}


        and discount_type = #{query.discountType}


        and franchisee_id =#{query.franchiseeId}



    limit #{query.offset}, #{query.size}

select  COUNT(1)
from t_coupon


        and name = #{query.name}


        and discount_type = #{query.discountType}


        and franchisee_id =#{query.franchiseeId}
