mapper.select:
select
id, name  from t_coupon

    del_flag=0

        and name like concat('%',#{name,jdbcType=VARCHAR},'%')


        and tenant_id = #{tenantId}


        and status = #{status}


order by id desc
limit #{offset}, #{size}

select
  id, name, status, apply_type,  discount_type, amount, discount, count, days,  description, superposition,
  uid, user_name, create_time, update_time, del_flag,franchisee_id,type
from t_coupon

    del_flag=0

        and name = #{query.name}


        and discount_type = #{query.discountType}


        and franchisee_id in

            #{id}



        and apply_type =#{query.applyType}


        and tenant_id = #{query.tenantId}


        AND `id` IN

            #{id}



order by create_time desc

    limit #{query.offset}, #{query.size}

select  COUNT(1)
from t_coupon

    del_flag=0

        and name = #{query.name}


        and discount_type = #{query.discountType}


        and franchisee_id in

            #{id}



        and apply_type =#{query.applyType}


        and tenant_id = #{query.tenantId}
