mapper.select:
SELECT count(1) as weekCount, FROM_UNIXTIME( create_time / 1000, '%Y-%m-%d' ) as weekDate FROM t_electricity_cabinet_order
and tenant_id = #{tenantId}
and create_time >=#{beginTime}
GROUP BY weekDate

SELECT count(1) as weekCount, FROM_UNIXTIME( tel.create_time / 1000, '%Y-%m-%d' ) as weekDate FROM t_electricity_member_card_order tel LEFT JOIN t_user_info tui ON tui.uid = tel.uid LEFT JOIN t_franchisee tf ON tf.id = tel.franchisee_id
and tel.tenant_id = #{tenantId}
and tel.create_time >=#{beginTime}
GROUP BY weekDate

SELECT count(1) as weekCount, FROM_UNIXTIME( create_time / 1000, '%Y-%m-%d' ) as weekDate FROM t_rent_battery_order
and tenant_id = #{tenantId}
and create_time >=#{beginTime}
GROUP BY weekDate

SELECT count(1) as couponCount, FROM_UNIXTIME( create_time / 1000, '%Y-%m-%d' ) as weekDate FROM t_user_coupon
and tenant_id = #{tenantId}
and create_time >=#{beginTime}
and status in
#{id}
GROUP BY weekDate

SELECT count(1) as turnover, FROM_UNIXTIME( create_time / 1000, '%Y-%m-%d' ) as weekDate FROM t_electricity_member_card_order where status=1
and tenant_id = #{tenantId}
and create_time >=#{beginTime}
GROUP BY weekDate

SELECT count(1) as turnover, FROM_UNIXTIME( create_time / 1000, '%Y-%m-%d' ) as weekDate FROM t_ele_deposit_order where status=1
and tenant_id = #{tenantId}
and create_time >=#{beginTime}
GROUP BY weekDate

SELECT count(1) as turnover, FROM_UNIXTIME( create_time / 1000, '%Y-%m-%d' ) as weekDate FROM t_ele_refund_order where status=1
and tenant_id = #{tenantId}
and create_time >=#{beginTime}
GROUP BY weekDate

SELECT count(1) as weekCount, FROM_UNIXTIME( create_time / 1000, '%Y-%m-%d' ) as weekDate FROM t_user_info where del_flag=0
and tenant_id = #{tenantId}
and create_time >=#{beginTime}
GROUP BY weekDate
