mapper.select:
select
id, division_account_id, car_model_id, tenant_id, del_flag, create_time, update_time
from t_division_account_car_model


        and id = #{id}


        and division_account_id = #{divisionAccountId}


        and car_model_id = #{carModelId}


        and tenant_id = #{tenantId}


        and del_flag = #{delFlag}


        and create_time = #{createTime}


        and update_time = #{updateTime}
