mapper.select:
select
id, name, hierarchy, franchisee_id, store_id, operator_rate, franchisee_rate, operator_rate_other,
franchisee_rate_other, store_rate, status, type, tenant_id, del_flag, create_time, update_time
from t_division_account_config
where del_flag=0

    and name like concat('%',#{name,jdbcType=VARCHAR},'%')


    and hierarchy = #{hierarchy}


    and franchisee_id = #{franchiseeId}


    and store_id = #{storeId}


    and status = #{status}


    and type = #{type}


    and tenant_id = #{tenantId}

order by id desc
limit #{offset}, #{size}

select
count(1)
from t_division_account_config
where del_flag=0

    and name like concat('%',#{name,jdbcType=VARCHAR},'%')


    and hierarchy = #{hierarchy}


    and franchisee_id = #{franchiseeId}


    and store_id = #{storeId}


    and status = #{status}


    and type = #{type}


    and tenant_id = #{tenantId}

select id, name
from t_division_account_config


        and name like concat('%',#{name,jdbcType=VARCHAR},'%')


        and tenant_id = #{tenantId}


order by id
limit #{offset}, #{size}

select
id, name, hierarchy, franchisee_id, store_id, operator_rate, franchisee_rate, operator_rate_other,
franchisee_rate_other, store_rate, status, type, tenant_id, del_flag,
create_time, update_time
from t_division_account_config


        and id = #{id}


        and name = #{name}


        and hierarchy = #{hierarchy}


        and franchisee_id = #{franchiseeId}


        and store_id = #{storeId}


        and franchisee_rate = #{franchiseeRate}


        and store_rate = #{storeRate}


        and status = #{status}


        and type = #{type}


        and tenant_id = #{tenantId}


        and del_flag = #{delFlag}


        and create_time = #{createTime}


        and update_time = #{updateTime}

SELECT dac.id, dac.name, dac.hierarchy, dac.franchisee_id, dac.store_id, dac.operator_rate, dac.franchisee_rate, dac.operator_rate_other, dac.franchisee_rate_other, dac.store_rate, dac.status, dac.type, dabm.ref_id
FROM t_division_account_config dac
left join t_division_account_battery_membercard dabm on dac.id=dabm.division_account_id
WHERE dac.del_flag=0 and dac.status=0
and dac.id = #{id}
and dac.franchisee_id = #{franchiseeId}
and dac.store_id = #{storeId}
and dac.type = #{type}
and dac.tenant_id = #{tenantId}
and dabm.ref_id = #{refId}

SELECT dac.id, dac.name, dac.hierarchy, dac.franchisee_id, dac.store_id, dac.operator_rate, dac.franchisee_rate, dac.operator_rate_other, dac.franchisee_rate_other, dac.store_rate, dac.status, dac.type, dabm.ref_id FROM t_division_account_config dac left join t_division_account_battery_membercard dabm on dac.id=dabm.division_account_id WHERE dac.del_flag=0 and dac.status =0
and dac.franchisee_id = #{franchiseeId}
and dac.store_id = #{storeId}
and dac.type = #{type}
and dac.tenant_id = #{tenantId}
and dabm.ref_id = #{refId}

SELECT dac.id, dac.name, dac.type, dac.hierarchy, dac.franchisee_id, dac.store_id, dac.operator_rate, dac.franchisee_rate, dac.operator_rate_other, dac.franchisee_rate_other, dac.store_rate, dac.status, dabm.type as packageType, dabm.ref_id FROM t_division_account_config dac left join t_division_account_battery_membercard dabm on dac.id = dabm.division_account_id WHERE dac.del_flag = 0 and dac.status = 0
and dac.franchisee_id = #{franchiseeId}
and dac.store_id = #{storeId}
and dabm.type = #{packageType}
and dac.tenant_id = #{tenantId}

SELECT dac.id as id, dac.name, dac.hierarchy, dac.franchisee_id, dac.store_id, dac.operator_rate, dac.franchisee_rate, dac.operator_rate_other, dac.franchisee_rate_other, dac.store_rate, dac.status, dac.type, dabm.ref_id FROM t_division_account_config dac left join t_division_account_battery_membercard dabm on dac.id=dabm.division_account_id WHERE dac.del_flag=0 and dac.status =0
and dac.franchisee_id = #{franchinseeId}
and dac.store_id = #{storeId}
and dabm.ref_id = #{refId}
and dac.tenant_id = #{tenantId}
order by dac.id desc limit 1
