mapper.select:
select
  id, name, hierarchy, franchisee_id, store_id, operator_rate, franchisee_rate, store_rate, status, type, tenant_id, del_flag, create_time, update_time
from t_division_account_config
where del_flag=0

    and name like concat('%',#{name,jdbcType=VARCHAR},'%')


    and hierarchy = #{hierarchy}


    and franchisee_id = #{franchiseeId}


    and store_id = #{storeId}


    and status = #{status}


    and type = #{type}


    and tenant_id = #{tenantId}

order by create_time desc
limit #{offset}, #{size}

select
count(1)
from t_division_account_config
