mapper.select:
select
  id, name, identity, type, create_time, update_time, del_flag, remark, need_modify
from electricity.t_ele_auth_entry


        and id = #{id}


        and name = #{name}


        and identity = #{identity}


        and type = #{type}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and del_flag = #{delFlag}


        and remark = #{remark}


        and need_modify = #{needModify}
