mapper.select:
select
id, name, franchisee_id, store_id, eid, tenant_id, type, create_time, update_time, json_rule
from t_ele_charge_config

    type = #{type}

        and franchisee_id = #{franchiseeId}



        and store_id = #{storeId}


        and eid = #{eid}



order by id desc limit 1

select
  id, name, franchisee_id, store_id, eid, tenant_id, type, create_time, update_time, json_rule
from t_ele_charge_config


        and id = #{id}


        and name = #{name}


        and franchisee_id = #{franchiseeId}


        and store_id = #{storeId}


        and eid = #{eid}


        and tenant_id = #{tenantId}


        and type = #{type}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and json_rule = #{jsonRule}

select
id, name, franchisee_id, store_id, eid, tenant_id, type, create_time, update_time, json_rule
from t_ele_charge_config

    tenant_id = #{tenantId}

        and name like concat('%', #{name}, '%')


        and franchisee_id = #{franchiseeId}


        and store_id = #{storeId}


        and eid = #{eid}


order by id desc
limit #{offset},#{size}

select
    count(1)
from t_ele_charge_config

    tenant_id = #{tenantId}

        and name = #{name}


        and franchisee_id = #{franchiseeId}


        and store_id = #{storeId}


        and eid = #{eid}
