mapper.select:
select
  id, pay_amount, create_time, update_time, uid, order_id, status, user_name, phone
from electricity.t_ele_deposit_order


        and id = #{id}


        and pay_amount = #{payAmount}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and uid = #{uid}


        and order_id = #{orderId}


        and status = #{status}


        and user_name = #{userName}


        and phone = #{phone}
