mapper.select:
select
id, pay_amount, create_time, update_time, uid, order_id, status, name, phone
from t_ele_deposit_order


        and order_id = #{query.orderId}


        and status = #{query.status}


        and name = #{query.name}


        and phone = #{query.phone}


        and create_time >=#{query.beginTime}


        and create_time <=#{query.endTime}


order by create_time desc
limit #{query.offset}, #{query.size}

select  COUNT(1)
from t_ele_deposit_order


        and order_id = #{query.orderId}


        and status = #{query.status}


        and name = #{query.name}


        and phone = #{query.phone}


        and create_time >=#{query.beginTime}


        and create_time <=#{query.endTime}
