        and edo.name = #{query.name}


        and edo.phone = #{query.phone}


        and edo.create_time >=#{query.beginTime}


        and edo.create_time <=#{query.endTime}


        and edo.tenant_id = #{query.tenantId}


        and edo.franchisee_id in

            #{id}



        and f.name = #{query.franchiseeName}


        and edo.store_id in

            #{id}



        and edo.deposit_type = #{query.depositType}


        and tecm.name = #{query.carModel}


        and edo.pay_type = #{query.payType}


        and ts.name like concat('%', #{query.storeName}, '%')

select
edo.id, edo.pay_amount, ero.create_time, ero.update_time, edo.uid,
edo.order_id, ero.status, edo.name, edo.phone, f.name as franchiseeName,
ero.refund_amount as refundAmount,ero.err_msg as errMsg, s.name as store_name
from t_ele_deposit_order edo
left join t_franchisee f on f.id = edo.franchisee_id
left join t_ele_refund_order as ero on ero.order_id= edo.order_id
left join t_store s on edo.store_id = s.id



        and edo.order_id = #{query.orderId}


        and edo.status = #{query.status}


        and edo.name = #{query.name}


        and edo.phone = #{query.phone}


        and edo.create_time >=#{query.beginTime}


        and edo.create_time <=#{query.endTime}


        and edo.tenant_id = #{query.tenantId}


        and edo.franchisee_id = #{query.franchiseeId}


        and edo.uid = #{query.uid}


        and ero.refund_order_type = #{query.refundOrderType}


        and f.name = #{query.franchiseeName}


        and s.id = #{uery.storeId}


order by ero.update_time desc
limit #{query.offset}, #{query.size}

select
edo.pay_amount, edo.create_time as payTime,ero.update_time as refundTime,ero.status as refundStatus
from t_ele_deposit_order edo
left join t_ele_refund_order as ero on ero.order_id= edo.order_id
where edo.status=1 and IFNULL(ero.status,'')  != 2

    and edo.order_id = #{query.orderId}


    and edo.status = #{query.status}


    and edo.name = #{query.name}


    and edo.phone = #{query.phone}


    and edo.create_time >=#{query.beginTime}


    and edo.create_time <=#{query.endTime}


    and edo.tenant_id = #{query.tenantId}


    and edo.franchisee_id = #{query.franchiseeId}


    and edo.uid = #{query.uid}


    and ero.refund_order_type = #{query.refundOrderType}

order by edo.update_time desc
limit #{query.offset}, #{query.size}

