mapper.select:
select member_card_name, status, user_name,phone, create_time, update_time,tenant_id, err_msg,
disable_member_card_no,card_days from t_ele_disable_member_card_record


        and tenant_id=#{query.tenantId}


        and uid=#{query.uid}


        and disable_member_card_no=#{query.disableMemberCardNo}


        and phone=#{query.phone}


        and status=#{query.status}


order by create_time desc
limit #{query.offset},#{query.size}

select count(1) from t_ele_disable_member_card_record


        and tenant_id=#{query.tenantId}


        and disable_member_card_no=#{query.disableMemberCardNo}


        and phone=#{query.phone}


        and status=#{query.status}
