mapper.select:
select
id, electricity_cabinet_id, cell_no, type, upgrade_version, history_version,
upgrade_sha256_value, status, upgrade_time, finish_time, err_msg, create_time, update_time,
session_id
from t_ele_new_ota_upgrade_history


        and id = #{id}


        and electricity_cabinet_id = #{electricityCabinetId}


        and cell_no = #{cellNo}


        and type = #{type}


        and upgrade_version = #{upgradeVersion}


        and history_version = #{historyVersion}


        and upgrade_sha256_value = #{upgradeSha256Value}


        and status = #{status}


        and upgrade_time = #{upgradeTime}


        and finish_time = #{finishTime}


        and err_msg = #{errMsg}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and session_id = #{sessionId}
