mapper.select:
select
id, electricity_cabinet_id, cell_no, type, status, upgrade_version, create_time, update_time
from t_ele_new_ota_upgrade


        and id = #{id}


        and electricity_cabinet_id = #{electricityCabinetId}


        and cell_no = #{cellNo}


        and type = #{type}


        and status = #{status}


        and upgrade_version = #{upgradeVersion}


        and create_time = #{createTime}


        and update_time = #{updateTime}
