mapper.select:
select
id, electricity_cabinet_id, cell_no, type, upgrade_version, history_version,
upgrade_sha256_value, status, upgrade_time, finish_time, err_msg, create_time, update_time,
session_id
from t_ele_ota_upgrade_history


        and id = #{id}


        and electricity_cabinet_id = #{electricityCabinetId}


        and cell_no = #{cellNo}


        and type = #{type}


        and upgrade_version = #{upgradeVersion}


        and history_version = #{historyVersion}


        and upgrade_sha256_value = #{upgradeSha256Value}


        and status = #{status}


        and upgrade_time = #{upgradeTime}


        and finish_time = #{finishTime}


        and err_msg = #{errMsg}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and session_id = #{sessionId}

select
  id, electricity_cabinet_id, cell_no, type, upgrade_version, history_version, upgrade_sha256_value, status, upgrade_time, finish_time, err_msg, create_time, update_time, session_id
from t_ele_ota_upgrade_history
where electricity_cabinet_id = #{eid} and session_id = #{sessionId}

    and cell_no = #{cellNo}


    and type = #{type}

select
teouh.id, ec.name as electricity_name , teouh.cell_no, teouh.type, teouh.upgrade_version, teouh.history_version,
teouh.upgrade_sha256_value, teouh.status, teouh.upgrade_time, teouh.finish_time, teouh.err_msg,
teouh.create_time, teouh.update_time, teouh.session_id
from t_ele_ota_upgrade_history teouh
left join t_electricity_cabinet ec on ec.id = teouh.electricity_cabinet_id


        and ec.name like concat('%', #{eleName}, '%')


        and cell_no = #{cellNo}


        and type = #{type}


        and upgrade_version = #{upgradeVersion}


        and upgrade_version = #{upgradeVersion}


        and history_version = #{historyVersion}


        and status = #{status}


        and wo.create_time BETWEEN #{startTime} and #{endTime}


order by create_time desc
limit #{offset}, #{size}

select
count(*)
from t_ele_ota_upgrade_history teouh
left join t_electricity_cabinet ec on ec.id = teouh.electricity_cabinet_id


        and ec.name like concat('%', #{eleName}, '%')


        and cell_no = #{cellNo}


        and type = #{type}


        and upgrade_version = #{upgradeVersion}


        and upgrade_version = #{upgradeVersion}


        and history_version = #{historyVersion}


        and status = #{status}


        and wo.create_time BETWEEN #{startTime} and #{endTime}
