mapper.select:
select
id, electricity_cabinet_id, cell_no, type, status, create_time, update_time
from t_ele_ota_upgrade


        and id = #{id}


        and electricity_cabinet_id = #{electricityCabinetId}


        and cell_no = #{cellNo}


        and type = #{type}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}

select
id, electricity_cabinet_id, cell_no, type, status, create_time, update_time
from t_ele_ota_upgrade
where electricity_cabinet_id = #{eid} and cell_no = #{cellNo}

    and type = #{type}
