mapper.select:
select
id, name, application_mode, charge_min_condition, charge_max_condition, charge_max_condition_v,
charge_min_condition_v, charging_num, normal_charge_a, normal_charge_v, open_heat_condition,
open_fan_condition, power_consumption_multiply, system_bar_status, revert_lock_status,
auto_temp_control_status, log_status, enable_battery_exception_check, server_heart_beat,
enable_battery_b_m_s_exception_check, create_time, update_time, tenant_id
from t_ele_param_setting_template


        and id = #{id}


        and name = #{name}


        and application_mode = #{applicationMode}


        and charge_min_condition = #{chargeMinCondition}


        and charge_max_condition = #{chargeMaxCondition}


        and charge_max_condition_v = #{chargeMaxConditionV}


        and charge_min_condition_v = #{chargeMinConditionV}


        and charging_num = #{chargingNum}


        and normal_charge_a = #{normalChargeA}


        and normal_charge_v = #{normalChargeV}


        and open_heat_condition = #{openHeatCondition}


        and open_fan_condition = #{openFanCondition}


        and power_consumption_multiply = #{powerConsumptionMultiply}


        and system_bar_status = #{systemBarStatus}


        and revert_lock_status = #{revertLockStatus}


        and auto_temp_control_status = #{autoTempControlStatus}


        and log_status = #{logStatus}


        and enable_battery_exception_check = #{enableBatteryExceptionCheck}


        and server_heart_beat = #{serverHeartBeat}


        and enable_battery_b_m_s_exception_check = #{enableBatteryBMSExceptionCheck}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and tenant_id = #{tenantId}
