mapper.select:
select
id, sn, e_name, eid, store_id, franchisee_id, tenant_id, report_time, create_time, type, sum_power, hour_power,
electric_charge
from t_ele_power


        and id = #{id}


        and sn = #{sn}


        and e_name = #{eName}


        and eid = #{eid}


        and store_id = #{storeId}


        and franchisee_id = #{franchiseeId}


        and tenant_id = #{tenantId}


        and report_time = #{reportTime}


        and create_time = #{createTime}


        and type = #{type}


        and sum_power = #{sumPower}


        and hour_power = #{hourPower}


        and electric_charge = #{electricCharge}

select
e_name, eid, report_time, create_time, type, sum_power, hour_power, electric_charge
from t_ele_power

    tenant_id = #{tenantId}

        and report_time >= #{startTime}


        and report_time <= #{endTime}


        and eid = #{eid}


order by report_time desc
limit #{offset}, #{size}

select from_unixtime(report_time/1000, '%Y-%m-%d') date,sum(hour_power) sumPower
from t_ele_power
where report_time >= #{startTime}
  and report_time <= #{endTime}
  and tenant_id = #{tenantId}
  and eid = #{eid}
group by date
order by date

select from_unixtime(report_time / 1000, '%Y-%m') date,sum(hour_power) sumPower,sum(electric_charge) sumElectricCharge
from t_ele_power
where report_time >= #{startTime}
  and report_time <= #{endTime}
  and tenant_id = #{tenantId}
  and eid = #{eid}
group by date
order by date

select type, sum(hour_power) sumPower
from t_ele_power
where report_time >= #{startTime}
  and report_time <= #{endTime}
  and tenant_id = #{tenantId}
  and eid = #{eid}
group by type;

select type, sum(hour_power) sumPower, sum(electric_charge) sumElectricCharge
from t_ele_power
where report_time >= #{startTime}
  and report_time <= #{endTime}
  and tenant_id = #{tenantId}
  and eid = #{eid}
group by type;
