select
  id, sn, e_name, eid, store_id, store_name, franchisee_id, franchisee_name, month_start_power, month_end_power, month_sum_power, month_sum_charge, json_charge, date, tenant_id, create_time
from t_ele_power_month_record


        and id = #{id}


        and sn = #{sn}


        and e_name = #{eName}


        and eid = #{eid}


        and store_id = #{storeId}


        and store_name = #{storeName}


        and franchisee_id = #{franchiseeId}


        and franchisee_name = #{franchiseeName}


        and month_start_power = #{monthStartPower}


        and month_end_power = #{monthEndPower}


        and month_sum_power = #{monthSumPower}


        and month_sum_charge = #{monthSumCharge}


        and json_charge = #{jsonCharge}


        and date = #{date}


        and tenant_id = #{tenantId}


        and create_time = #{createTime}

select
sn, e_name, eid, store_name, franchisee_name, month_start_power, month_end_power, month_sum_power, month_sum_charge,
json_charge, date, create_time
from t_ele_power_month_record

    tenant_id = #{tenantId}

        and eid = #{eid}


        and store_id = #{storeId}


        and franchisee_id = #{franchiseeId}


        and date >= #{startDate}



        and date < #{endDate}


order by id desc
limit #{offset},#{size}

