mapper.select:
select
  id, refund_order_no, order_id, pay_amount, refund_amount, status, err_msg, del_flag, create_time, update_time
from electricity.t_ele_refund_order


        and id = #{id}


        and refund_order_no = #{refundOrderNo}


        and order_id = #{orderId}


        and pay_amount = #{payAmount}


        and refund_amount = #{refundAmount}


        and status = #{status}


        and err_msg = #{errMsg}


        and del_flag = #{delFlag}


        and create_time = #{createTime}


        and update_time = #{updateTime}
