mapper.select:
select
  id, uid, entry_id, value, create_time, status, del_flag, update_time
from electricity.t_ele_user_auth


        and id = #{id}


        and uid = #{uid}


        and entry_id = #{entryId}


        and value = #{value}


        and create_time = #{createTime}


        and status = #{status}


        and del_flag = #{delFlag}


        and update_time = #{updateTime}

mapper.update:
update electricity.t_ele_user_auth
set status = #{authStatus},update_time = #{updateTime}
where uid = #{uid} and del_flag=0
