mapper.select:
select tuer.id as id, tuer.uid as uid, tui.name as name, tui.phone as phone, tuer.tenant_id as tenantId, tuer.sign_flow_id as signFlowId, tuer.sign_finish_status as signFinishStatus, tuer.file_id as fileId, tuer.file_name as fileName, tuer.del_flag as delFlag, tuer.create_time as createTime, tuer.update_time as updateTime from t_user_esign_record tuer left join t_user_info tui on tui.uid = tuer.uid
tuer.tenant_id = #{tenantId}
and tui.name like concat('%', #{name}, '%')
and tui.phone like concat('%', #{phone}, '%')
and tuer.sign_finish_status = #{signFinishStatus}
order by tuer.create_time desc limit #{offset}, #{size}

select count(1) from t_user_esign_record tuer left join t_user_info tui on tui.uid = tuer.uid
tuer.tenant_id = #{tenantId}
and tui.name like concat('%', #{name}, '%')
and tui.phone like concat('%', #{phone}, '%')
and tuer.sign_finish_status = #{signFinishStatus}
