mapper.select:
select
  id, electricity_cabinet_id, electricity_cabinet_name, cell_no, msg, type, status, operate_uid, create_time, update_time
from electricity.t_ele_warn_msg


        and electricity_cabinet_id = #{query.electricityCabinetId}


        and type = #{query.type}


        and status = #{query.status}


        and electricity_cabinet_id in

            #{id}



order by id desc
limit #{query.offset}, #{query.size}
