mapper.select:
select
id, electricity_cabinet_id, electricity_cabinet_name, cell_no, msg, type, status, operate_uid, create_time,
update_time
from t_ele_warn_msg


        and electricity_cabinet_id = #{query.electricityCabinetId}


        and type = #{query.type}


        and status = #{query.status}


        and tenant_id = #{query.tenantId}


        and cell_no = #{query.cellNo}


        and electricity_cabinet_id in

            #{id}



        and electricity_cabinet_id in (select id from t_electricity_cabinet where name =
        #{query.electricityCabinetName})


order by id desc
limit #{query.offset}, #{query.size}

select
count(*)
from t_ele_warn_msg


        and electricity_cabinet_id = #{query.electricityCabinetId}


        and type = #{query.type}


        and status = #{query.status}


        and tenant_id = #{query.tenantId}


        and cell_no = #{query.cellNo}


        and electricity_cabinet_id in

            #{id}



        and electricity_cabinet_id in (select id from t_electricity_cabinet where name =
        #{query.electricityCabinetName})
