mapper.select:
		select b.id, b.sn,b.power, b.voltage, b.capacity, b.status, b.create_time, b.update_time, b.del_flag, b.tenant_id, b.electricity_cabinet_id,
		b.uid,b.model,b.charge_status,b.iot_card_number
		from t_electricity_battery b
		where b.del_flag = 0 and b.tenant_id = #{query.tenantId}

			and b.sn like concat('%',#{query.sn,jdbcType=VARCHAR},'%')


			and b.status =#{query.status}


			and b.charge_status =#{query.chargeStatus}


			and b.tenant_id = #{query.tenantId}


 			and b.electricity_cabinet_name like  concat('%',#{query.electricityCabinetName},'%') and status = 0


			and b.id in

				#{id}



			and b.id in (select fbeb.electricity_battery_id from t_franchisee_bind_electricity_battery fbeb left join t_franchisee f on fbeb.franchisee_id = f.id where f.name like concat('%',#{query.franchiseeName},'%'))

		order by create_time desc
		limit #{offset}, #{size}

select b.id, b.sn,b.power, b.voltage, b.capacity, b.status, b.create_time, b.update_time, b.del_flag, b.tenant_id, b.electricity_cabinet_id,
b.uid,b.model,b.charge_status
from t_electricity_battery b
where b.del_flag = 0 and b.tenant_id = #{tenantId}
and id not in(
     select electricity_battery_id from t_franchisee_bind_electricity_battery fbeb where 1=1

		 and fbeb.franchisee_id != #{franchiseeId}

     )
order by create_time desc
limit #{offset}, #{size}

		select COUNT(1)
		from t_electricity_battery b
		where b.del_flag = 0

			and b.tenant_id =#{query.tenantId}


			and b.sn like concat('%',#{query.sn,jdbcType=VARCHAR},'%')


			and b.status =#{query.status}


			and b.charge_status =#{query.chargeStatus}


			and b.tenant_id = #{query.tenantId}


            and b.electricity_cabinet_name like  concat('%',#{query.electricityCabinetName},'%') and status = 0


			and b.id in

				#{id}



            and b.id in (select fbeb.electricity_battery_id from t_franchisee_bind_electricity_battery fbeb left join t_franchisee f on fbeb.franchisee_id = f.id where f.name like concat('%',#{query.franchiseeName},'%'))

select b.sn, b.electricity_cabinet_name, b.borrow_expire_time, u.name, u.phone, b.status, b.tenant_id
FROM t_electricity_battery b left join t_user_info u on b.uid = u.uid
WHERE b.borrow_expire_time < #{curTime} and b.del_flag = 0 and b.status in(2,3,4) limit #{offset}, #{size}

       select sn,exchange_count as useCount,status from t_electricity_battery b
       where b.del_flag = 0

			and b.tenant_id =#{query.tenantId}


			and b.sn like concat('%',#{query.batterySn,jdbcType=VARCHAR},'%')


			and b.update_time >=#{query.beginTime}


			and b.update_time <=#{query.endTime}


			and b.id in (select fbeb.electricity_battery_id from t_franchisee_bind_electricity_battery fbeb where fbeb.franchisee_id=#{query.franchiseeId})

		order by exchange_count desc
		limit #{query.offset}, #{query.size}

select sn,exchange_count as useCount,status from t_electricity_battery b
where b.del_flag = 0

	and b.tenant_id =#{query.tenantId}


	and b.sn like concat('%',#{query.batterySn,jdbcType=VARCHAR},'%')


	and b.update_time >=#{query.beginTime}


	and b.update_time <=#{query.endTime}


	and b.id in (select fbeb.electricity_battery_id from t_franchisee_bind_electricity_battery fbeb where fbeb.franchisee_id=#{query.franchiseeId})

select b.id, b.sn,b.power, b.voltage, b.capacity, b.status, b.create_time, b.update_time, b.del_flag, b.tenant_id, b.electricity_cabinet_id, b.uid,b.model,b.charge_status,b.iot_card_number,b.longitude,b.latitude from t_electricity_battery b where b.del_flag = 0 and b.tenant_id = #{query.tenantId}
and b.sn like concat('%',#{query.sn,jdbcType=VARCHAR},'%')
and b.status =#{query.status}
order by create_time desc
