mapper.select:
select

from t_electricity_cabinet_box_lock
where  tenant_id = #{tenantId}

    and name like concat('%',#{name},'%')


    and sn  like concat('%',#{sn},'%')


    and address like concat('%',#{address},'%')


    and lock_type = #{lockType}


    and area_id = #{areaId}


    and franchisee_id = #{franchiseeId}


    AND franchisee_id IN

        #{franchiseeId}



    and store_id = #{storeId}


    and lock_status_change_time >=#{lockStatusChangeTimeStart}


    and lock_status_change_time <=#{lockStatusChangeTimeEnd}

limit #{offset},#{size}

select count(1)
from t_electricity_cabinet_box_lock
where tenant_id = #{tenantId}

    and name like concat('%',#{name},'%')


    and sn like concat('%',#{sn},'%')


    and address like concat('%',#{address},'%')


    and lock_type = #{lockType}


    and area_id = #{areaId}


    and franchisee_id = #{franchiseeId}


    AND franchisee_id IN

        #{franchiseeId}



    and store_id = #{storeId}


    and lock_status_change_time >=#{lockStatusChangeTimeStart}


    and lock_status_change_time <=#{lockStatusChangeTimeEnd}
