mapper.select:
select
  id, electricity_cabinet_id, cell_no, electricity_battery_id, usable_status, box_status, status, create_time, update_time, del_flag
from electricity.t_electricity_cabinet_box


        and id = #{id}


        and electricity_cabinet_id = #{electricityCabinetId}


        and cell_no = #{cellNo}


        and electricity_battery_id = #{electricityBatteryId}


        and usable_status = #{usableStatus}


        and box_status = #{boxStatus}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and del_flag = #{delFlag}

mapper.update:
update electricity.t_electricity_cabinet_box
set del_flag = 1
where electricityCabinetId = #{id}

 update electricity.t_electricity_cabinet_box
set box_status = 0
where electricityCabinetId = #{id}
