mapper.select:
select id, electricity_cabinet_id, cell_no, electricity_battery_id, usable_status, status,is_lock,is_fan,temperature,is_heat,is_light, create_time, update_time, del_flag from electricity.t_electricity_cabinet_box where del_flag=0
and electricity_cabinet_id = #{query.electricityCabinetId}
order by -cell_no DESC

mapper.update:
update electricity.t_electricity_cabinet_box
set del_flag = 1, update_time = #{updateTime}
where electricity_cabinet_id = #{id}
