mapper.select:
select
id, name,sn, address, longitude, latitude, product_key, device_name, usable_status,
online_status, model_id, version, fully_charged, service_phone, business_time, create_time,
update_time, del_flag,tenant_id,store_id,franchisee_id, exchange_type,stock_status,warehouse_id
from t_electricity_cabinet
where del_flag=0

    and name like concat('%',#{query.name,jdbcType=VARCHAR},'%')


    and address like concat('%',#{query.address,jdbcType=VARCHAR},'%')


    and usable_status = #{query.usableStatus}


    and id =#{query.id}


    and online_status = #{query.onlineStatus}


    and stock_status = #{query.stockStatus}


    and tenant_id = #{query.tenantId}


    and create_time >=#{query.beginTime}


    and create_time <=#{query.endTime}


    and id in

        #{id}



    AND `franchisee_id` IN

        #{franchiseeId}



    AND `store_id` IN

        #{storeId}


order by id desc
limit #{query.offset}, #{query.size}

		select count(1)
		from t_electricity_cabinet
		where del_flag=0

			and name like concat('%',#{query.name,jdbcType=VARCHAR},'%')


			and address like concat('%',#{query.address,jdbcType=VARCHAR},'%')


			and usable_status = #{query.usableStatus}


			and online_status = #{query.onlineStatus}


            and stock_status = #{query.stockStatus}


			and tenant_id = #{query.tenantId}


			and create_time >=#{query.beginTime}


			and create_time <=#{query.endTime}


            and sn like concat('%',#{query.sn, jdbcType=VARCHAR},'%')


            and model_id = #{query.modelId}


			and id in

				#{id}

select tec.id, tec.name,tec.sn, tec.address, tec.longitude, tec.latitude, tec.product_key, tec.device_name, tec.device_secret,
tec.usable_status, tec.online_status, tec.model_id, tec.version,
tec.fully_charged, tec.service_phone, tec.business_time, tec.create_time, tec.update_time, ROUND(
6378.138 * 2 * ASIN(
SQRT(
POW(
SIN(
(
#{query.lat} * PI() / 180 - tec.latitude * PI() / 180
) / 2
),
2
) + COS(#{query.lat} * PI() / 180) * COS(tec.latitude * PI() / 180) * POW(
SIN(
(
#{query.lon} * PI() / 180 - tec.longitude * PI() / 180
) / 2
),
2
)
)
) * 1000
) AS distance
from t_electricity_cabinet tec
left join t_store ts on  tec.store_id=ts.id
where tec.del_flag = 0

    and tec.name like concat('%',#{query.name,jdbcType=VARCHAR},'%')


    and ts.franchisee_id = #{query.franchiseeId}


    and tec.tenant_id = #{query.tenantId}


     and tec.`usable_status` = #{query.usableStatus}


    having distance < #{query.distance}

order by distance asc

select tec.id,tec.sn , tec.service_phone ,tec.fully_charged ,tec.business_time, tec.name,tec.address, tec.longitude, tec.latitude, ROUND( 6378.138 * 2 * ASIN( SQRT( POW( SIN( ( #{lat} * PI() / 180 - tec.latitude * PI() / 180
) / 2 ), 2 ) + COS(#{lat} * PI() / 180) * COS(tec.latitude * PI() / 180) * POW( SIN( ( #{lon} * PI() / 180 - tec.longitude * PI() / 180 ) / 2
), 2 ) ) ) * 1000 ) AS distance
from t_electricity_cabinet tec
left join t_store ts on  tec.store_id=ts.id
where tec.del_flag = 0

    and ( tec.address like concat('%',#{address,jdbcType=VARCHAR},'%') or tec.name like
    concat('%',#{address,jdbcType=VARCHAR},'%') )


    and ts.franchisee_id = #{franchiseeId}


    and tec.tenant_id = #{tenantId}


    and tec.`usable_status` = #{usableStatus}

order by distance asc
limit #{offset}, #{size}

