select
ts.franchisee_id as franchisee_id,
tec.id, tec.name, tec.sn, tec.product_key, tec.device_name, tec.address, tec.usable_status, tec.online_status, tec.model_id, tec.version, tec.fully_charged, tec.update_time, tec.tenant_id, tec.store_id from t_electricity_cabinet tec
left join t_store ts on tec.store_id=ts.id
where tec.del_flag=0
and tec.name like concat('%',#{name,jdbcType=VARCHAR},'%')
and tec.address like concat('%',#{address,jdbcType=VARCHAR},'%')
and tec.sn like concat('%',#{sn,jdbcType=VARCHAR},'%')
