and tec.online_status = #{onlineStatus}
and tec.tenant_id = #{tenantId}
and ts.franchisee_id = #{franchiseeId}
and tec.store_id = #{storeId}
and tec.id in
#{id}
order by tec.id desc limit #{offset}, #{size}

select
ts.franchisee_id as franchisee_id,
