select
any_value(ts.franchisee_id) as franchisee_id,
any_value(tec.id) AS id, any_value(tec.name) AS name, any_value(tec.sn) AS sn, any_value(tec.product_key) as product_key, any_value(tec.device_name) as device_name, any_value(tec.address) AS address, any_value(tec.usable_status) AS usable_status, any_value(tec.online_status) AS online_status, any_value(tec.model_id) AS model_id, any_value(tec.version) AS version, any_value(tec.fully_charged) AS fully_charged, any_value(tec.update_time) AS update_time, any_value(tec.tenant_id) AS tenant_id, any_value(tec.store_id) AS store_id, any_value(tec.del_flag) AS del_flag, any_value(ecb.power) AS power from t_electricity_cabinet_box ecb LEFT JOIN t_electricity_cabinet tec ON ecb.electricity_cabinet_id = tec.id
left join t_store ts on tec.store_id=ts.id
where tec.tenant_id = #{tenantId}
and ecb.usable_status = #{usableStatusCell}
and tec.name like concat('%',#{name,jdbcType=VARCHAR},'%')
and tec.address like concat('%',#{address,jdbcType=VARCHAR},'%')
and tec.sn like concat('%',#{sn,jdbcType=VARCHAR},'%')
and tec.usable_status = #{usableStatus}
and tec.online_status = #{onlineStatus}
and ts.franchisee_id = #{franchiseeId}
and tec.store_id = #{storeId}
and tec.id in
#{id}
GROUP BY ecb.electricity_cabinet_id having del_flag=0
and sum( status = 0 ) / count( electricity_cabinet_id ) <= #{lowChargeRate}
and sum( status = 0 ) / count( electricity_cabinet_id ) >= #{fullChargeRate}
limit #{offset}, #{size}

