any_value(tec.fully_charged) fully_charged,
any_value(tec.del_flag) del_flag,
any_value(ecb.power) power
from t_electricity_cabinet_box ecb
LEFT JOIN t_electricity_cabinet tec ON ecb.electricity_cabinet_id = tec.id

    left join t_store ts on tec.store_id=ts.id

