mapper.select:
select
id,  name, address, longitude, latitude, product_key, device_name, device_secret, usable_status,
 online_status, model_id, version, fully_charged, service_phone, business_time, create_time,
update_time, del_flag,tenant_id
from t_electricity_cabinet
where del_flag=0 and  tenant_id = #{query.tenantId}

    and name = #{query.name}


    and address like concat('%',#{query.address,jdbcType=VARCHAR},'%')


    and usable_status = #{query.usableStatus}


    and online_status = #{query.onlineStatus}


    and tenant_id = #{query.tenantId}


    and create_time >=#{query.beginTime}


    and create_time <=#{query.endTime}


    and id in

        #{id}


order by create_time desc
limit #{query.offset}, #{query.size}

select id,  name, address, longitude, latitude, product_key, device_name, device_secret,
usable_status, online_status, model_id, version,
fully_charged, service_phone, business_time, create_time, update_time, ROUND(
6378.138 * 2 * ASIN(
SQRT(
POW(
SIN(
(
#{query.lat} * PI() / 180 - latitude * PI() / 180
) / 2
),
2
) + COS(#{query.lat} * PI() / 180) * COS(latitude * PI() / 180) * POW(
SIN(
(
#{query.lon} * PI() / 180 - longitude * PI() / 180
) / 2
),
2
)
)
) * 1000
) AS distance
from t_electricity_cabinet
where del_flag = 0

    having distance < #{query.distance}

order by distance asc

select
id, name
from t_electricity_cabinet
where del_flag=0

    and tenant_id = #{query.tenantId}


    and id in

        #{id}


limit #{offset}, #{size}

select id, name, address, longitude, latitude, product_key, device_name, device_secret, usable_status, online_status, model_id, version, fully_charged, service_phone, business_time, create_time, update_time, del_flag from t_electricity_cabinet where del_flag=0
and id in
#{id}
and tenant_id = #{tenantId}
