mapper.select:
select
  id, sn, name, area_id, address, longitude, latitude, product_key, device_name, device_secret, usable_status, power_status, online_status, model_id, version, fully_charged, service_phone, business_time, create_time, update_time, del_flag
from electricity.t_electricity_cabinet
where del_flag=0

    and sn = #{query.sn}


    and name = #{query.name}


    and area_id = #{query.areaId}


    and address = #{query.address}


    and usable_status = #{query.usableStatus}


    and power_status = #{query.powerStatus}


    and online_status = #{query.onlineStatus}

order by update_time
limit #{query.offset}, #{query.size}

select
  id, sn, name, area_id, address, longitude, latitude, product_key, device_name, device_secret, usable_status, power_status, online_status, model_id, version, fully_charged, service_phone, business_time, create_time, update_time, del_flag
from electricity.t_electricity_cabinet


        and id = #{id}


        and sn = #{sn}


        and name = #{name}


        and area_id = #{areaId}


        and address = #{address}


        and longitude = #{longitude}


        and latitude = #{latitude}


        and product_key = #{productKey}


        and device_name = #{deviceName}


        and device_secret = #{deviceSecret}


        and usable_status = #{usableStatus}


        and power_status = #{powerStatus}


        and online_status = #{onlineStatus}


        and model_id = #{modelId}


        and version = #{version}


        and fully_charged = #{fullyCharged}


        and service_phone = #{servicePhone}


        and business_time = #{businessTime}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and del_flag = #{delFlag}

select  id, sn, name, area_id, address, longitude, latitude, product_key, device_name, device_secret, usable_status, power_status, online_status, model_id, version,
fully_charged, service_phone, business_time, create_time, update_time,  ROUND(
6378.138 * 2 * ASIN(
SQRT(
POW(
SIN(
(
#{query.lat} * PI() / 180 - latitude * PI() / 180
) / 2
),
2
) + COS(#{query.lat} * PI() / 180) * COS(latitude * PI() / 180) * POW(
SIN(
(
#{query.lon} * PI() / 180 - longitude * PI() / 180
) / 2
),
2
)
)
) * 1000
) AS distance
from electricity.t_electricity_cabinet
where del_flag = 0

    having distance < #{query.distance}

order by distance
limit #{query.offset}, #{query.limit}
