mapper.select:
select
id, sn, name,  address, longitude, latitude, product_key, device_name, device_secret, usable_status,
power_status, online_status, model_id, version, fully_charged, service_phone, business_time, create_time,
update_time, del_flag
from electricity.t_electricity_cabinet
where del_flag=0

    and sn = #{query.sn}


    and name = #{query.name}


    and address like concat('%',#{query.address,jdbcType=VARCHAR},'%')


    and usable_status = #{query.usableStatus}


    and power_status = #{query.powerStatus}


    and online_status = #{query.onlineStatus}


    and create_time >=#{query.beginTime}


    and create_time <=#{query.endTime}

order by create_time desc

select
tec.id as id, tec.sn as sn, tec.name as name, tec.address as address, tec.longitude as longitude, tec.latitude as latitude,
tec.product_key as product_key, tec.device_name as device_name, tec.device_secret as device_secret, tec.usable_status as usable_status,
tec.power_status as power_status, tec.online_status as online_status, tec.model_id as model_id, tec.version as version,
tec.fully_charged as fully_charged, tec.service_phone as service_phone, tec.business_time as business_time, tec.create_time as create_time,
tec.update_time as update_time, tec.del_flag as del_flag
from electricity.t_electricity_cabinet tec
left join electricity.t_electricity_cabinet_bind as tecb on tec.id=tecb.electricity_cabinet_id
where del_flag=0

    and tec.sn = #{query.sn}


    and tec.name = #{query.name}


    and tec.address like concat('%',#{query.address,jdbcType=VARCHAR},'%')


    and tec.usable_status = #{query.usableStatus}


    and tec.power_status = #{query.powerStatus}


    and tec.online_status = #{query.onlineStatus}


    and tec.create_time >=#{query.beginTime}


    and tec.create_time <=#{query.endTime}


    and tecb.uid in

        #{id}


order by tec.create_time desc

select id, sn, name, address, longitude, latitude, product_key, device_name, device_secret,
usable_status, power_status, online_status, model_id, version,
fully_charged, service_phone, business_time, create_time, update_time, ROUND(
6378.138 * 2 * ASIN(
SQRT(
POW(
SIN(
(
#{query.lat} * PI() / 180 - latitude * PI() / 180
) / 2
),
2
) + COS(#{query.lat} * PI() / 180) * COS(latitude * PI() / 180) * POW(
SIN(
(
#{query.lon} * PI() / 180 - longitude * PI() / 180
) / 2
),
2
)
)
) * 1000
) AS distance
from electricity.t_electricity_cabinet
where del_flag = 0

    having distance < #{query.distance}

order by distance asc
