select from_unixtime(create_time / 1000, '%Y-%m-%d') date, count(*) as count
from t_electricity_cabinet where create_time >= #{startTimeMilliDay} and create_time <=#{endTimeMilliDay}

    and id in

        #{id}



    and tenant_id = #{tenantId}

group by date
order by date desc

